import {
  SET_UKRAINE_STATISTICS_START,
  IS_UKRAINE_LOADING_SUCCESS,
  IS_UKRAINE_ERROR,
} from "./Constants";
import { handleActions as ukraineReducer } from "../../utils/redux-actions";

export const initialState = {
  NewConfirmed: 0,
  TotalConfirmed: 0,
  TotalDeaths: 0,
  NewRecovered: 0,
  isLoading: false,
  isError: false,
};

export default ukraineReducer({
  [SET_UKRAINE_STATISTICS_START]: (state, action) => {    
    return {
      ...state,
      isLoading: true,
    };
  },
  [IS_UKRAINE_LOADING_SUCCESS]: (state, {payload}) => {
    const statistic = payload.data.Countries.filter((c) => c.Country === "Ukraine");
    const newStatistic = statistic[0];

    state.NewConfirmed = newStatistic.NewConfirmed;
    state.TotalConfirmed = newStatistic.TotalConfirmed;
    state.TotalDeaths = newStatistic.TotalDeaths;
    state.NewRecovered = newStatistic.NewRecovered;
    state.isLoading = false;
  },
  [IS_UKRAINE_ERROR]: (state, action) => {
    return {
      ...state,
      isError: action.payload,
    };
  },
});
